import React, { Component } from "react";
import List from "./list";
import Head from "next/head";

class App extends Component {
  state = {
    term: "",
    items: []
  };
  onChange = action => {
    this.setState({ term: action.target.value });
  };

  onSubmit = action => {
    action.preventDefault();
    this.setState({
      term: "",
      items: [...this.state.items, this.state.term]
    });
  };
  onDelete = action => {
    const items = this.state.items.filter(c => c !== action);
    this.setState({ items });
  };

  render() {
    return (
      <div className="d-flex justify-content-center">
        <Head>
          <title>Todo-list App</title>
          <link
            rel="stylesheet"
            href="https://bootswatch.com/4/cerulean/bootstrap.min.css"
          />
        </Head>
        <p style={{ color: "green" }} className="h1">
          What will you like to do?
        </p>
        <div className="d-inline-flex p-2 justify-content-center">
          <div className="form-group">
            <form onSubmit={this.onSubmit}>
              <input value={this.state.term} onChange={this.onChange} />

              <button className="btn btn-primary m-2">Submit</button>
            </form>
          </div>
        </div>

        <List items={this.state.items} onDelete={this.onDelete} />
      </div>
    );
  }
}

export default App;
